mapper.select:
select sa.id, sa.community_id, sa.monitor_id, sa.tenement_id, sa.printscreen, sa.monitor_name, sa.installation_site, sa.tenement_name, sa.alarm_date, sa.alarm_time, sa.status, sa.del_status, sa.create_time from bh_high_toss_act_alarm as sa
sa.del_status=0
AND sa.installation_site = #{installationSite, jdbcType="VARCHAR"}
AND ( sa.monitor_name LIKE CONCAT('%', #{keyword}, '%'))
AND sa.alarm_date >= #{beginDate,jdbcType=TIMESTAMP}
AND sa.alarm_date <= concat(#{endDate,jdbcType=TIMESTAMP},' 23:59:59')
order by create_time DESC
LIMIT #{pageNum},#{pageSize}

select count(1) from bh_high_toss_act_alarm as sa
sa.del_status=0
AND sa.installation_site = #{installationSite, jdbcType="VARCHAR"}
AND ( sa.monitor_name LIKE CONCAT('%', #{keyword}, '%'))
AND sa.alarm_date >= #{beginDate,jdbcType=TIMESTAMP}
AND sa.alarm_date <= concat(#{endDate,jdbcType=TIMESTAMP},' 23:59:59')
