mapper.select:
select sa.id, sa.community_id, sa.monitor_name, sa.monitor_type, sa.monitor_account, sa.monitor_password, sa.manufacturer, sa.resolution_ratio, sa.visit_address, sa.distinguish_content, sa.installation_site, sa.sort, sa.status, sa.del_status, sa.create_time from bh_monitor_aids as sa
sa.del_status=0
AND sa.monitor_type = #{monitorType}
AND ( sa.monitor_name LIKE CONCAT('%', #{keyword}, '%'))
order by create_time DESC
LIMIT #{pageNum},#{pageSize}
