mapper.insert:

  SELECT LAST_INSERT_ID()

insert into bh_slideshow (community_id, slideshow_pic, timing_play, 
  begin_time, end_time, status, 
  schedule, del_status, create_time
  )
values (#{communityId,jdbcType=INTEGER}, #{slideshowPic,jdbcType=VARCHAR}, #{timingPlay,jdbcType=INTEGER}, 
  #{beginTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{status,jdbcType=INTEGER}, 
  #{schedule,jdbcType=INTEGER}, #{delStatus,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select sa.id, sa.community_id, sa.slideshow_pic, sa.timing_play, sa.begin_time, sa.end_time, sa.schedule, sa.status, sa.del_status, sa.create_time from bh_slideshow as sa
sa.del_status=0
AND sa.schedule = #{schedule}
AND sa.begin_time >= #{beginDate,jdbcType=TIMESTAMP}
AND sa.end_time <= concat(#{endDate,jdbcType=TIMESTAMP},' 23:59:59')
order by create_time DESC
LIMIT #{pageNum},#{pageSize}

select count(1) from bh_slideshow as sa
sa.del_status=0
AND sa.schedule = #{schedule}
AND sa.begin_time >= #{beginDate,jdbcType=TIMESTAMP}
AND sa.end_time <= concat(#{endDate,jdbcType=TIMESTAMP},' 23:59:59')
