mapper.select:
select sa.id, sa.tenement_id, sa.monitor_id, sa.tenement_pic, sa.tenement_name, sa.address, sa.phone, sa.tenement_type, sa.pass_date, sa.pass_time, sa.pass_address, sa.status, sa.del_status, sa.create_time from bh_tenement_pass_record as sa
sa.del_status=0
AND sa.tenement_name = #{tenementName}
AND sa.pass_date >= #{beginDate,jdbcType=TIMESTAMP}
AND sa.pass_date <= concat(#{endDate,jdbcType=TIMESTAMP},' 23:59:59')
order by create_time DESC
LIMIT #{pageNum},#{pageSize}

select count(1) from bh_tenement_pass_record as sa
sa.del_status=0
AND sa.tenement_name = #{tenementName}
AND sa.pass_date >= #{beginDate,jdbcType=TIMESTAMP}
AND sa.pass_date <= concat(#{endDate,jdbcType=TIMESTAMP},' 23:59:59')
